import React, { Component } from "react";

export default class Produk extends Component {
  constructor(props) {
    super(props);
    this.state = {
      stock: props.stock,
      sub: "Beli",
      status: "Tersedia",
      disabled: false,
    };
  }

  buttonBeli = () => {
    this.setState({
      stock: this.state.stock - 1,
    });
    if (this.state.stock === 1) {
      this.setState({
        status: "Habis",
        disabled: true,
      });
    }
  };

  render() {
    return (
      <div className="box-stock">
        <h2>{this.props.nama}</h2>
        <img
          src="https://source.unsplash.com/640x480?computer"
          alt="Technology"
        />
        <p>Harga: {this.props.harga}</p>
        <p>Stok: {this.state.stock}</p>
        <button
          className="btn-click"
          onClick={this.buttonBeli}
          disabled={this.state.disabled}
        >
          Beli
        </button>
        <p>Status: {this.state.status}</p>
      </div>
    );
  }
}
