import Swal from "sweetalert2";

export function swal_alert(status_code, title, message, callback = true) {
  let icon = 500;
  if (status_code === 201) icon = "success";
  else if (status_code === 403) icon = "warning";
  else icon = "error";

  Swal.fire({
    icon: icon,
    title: title,
    text: message,
    showConfirmButton: false,
    timer: 1500,
  }).then(function () {
    if (callback) window.history.back();
    else window.location.reload();
  });
}
