import React, { Component } from "react";
import DetailProduk from "./DetailProduk";

export default class Produk extends Component {
  constructor(props) {
    super(props);
    this.state = {
      stock: props.stock,
      sub: "Beli",
      status: "Tersedia",
      disabled: false,
    };
  }

  buttonBeli = () => {
    this.setState({
      stock: this.state.stock - 1,
    });
    if (this.state.stock === 1) {
      this.setState({
        status: "Habis",
        disabled: true,
      });
    }
  };

  render() {
    return (
      <div>
        <DetailProduk />;
      </div>
    );
  }
}
