import React, { PureComponent } from "react";
import axios from "axios";
import qr from "querystring";
import { Table, Button, Container, NavLink, Alert } from "reactstrap";

const api = "http://localhost/restperpus/";
const apikey = "123456789";

export default class ListBuku extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      databuku: [],
      response: "",
      display: "none",
    };
  }

  // Component untuk load data dari server
  componentDidMount() {
    axios
      .get(api + `/buku?rest-key=${apikey}`, {
        headers: {
          Authorization: `Basic ${token}`,
        },
      })
      .then((res) => {
        this.setState({
          databuku: res.data.result,
        });
      });
  }

  render() {
    return (
      <Container>
        <h2>Data Buku</h2>
        <hr />
        <Table className="table-bordered">
          <thead>
            <tr>
              <th>Judul</th>
              <th>Penulis</th>
              <th>Penerbit</th>
              <th>Aksi</th>
            </tr>
          </thead>
          <tbody>
            {this.state.databuku.map((item) => (
              <tr key={item.id_buku}>
                <td>{item.judul}</td>
                <td>{item.penulis}</td>
                <td>{item.penerbit}</td>
                <td>Edit | Hapus</td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>
    );
  }
}
